package com.vesmile.webappdemo;import java.util.Random;public class FmRandomPaCa {		public int randomPa(){		Random r= new Random();		int pa = 0;		while(pa<176){		  pa = r.nextInt(200);		}		return pa;	}		public int randomCa(){		Random r= new Random();		int ca = 0;		while(ca<120){		  ca = r.nextInt(140);		}		return ca;	}		public static void main(String[] args) {		FmRandomPaCa f = new FmRandomPaCa();		System.out.println("o={========> The new greatest football player is creating:");		System.out.println("o={========> The CA of player is " + f.randomCa());		System.out.println("o={========> The PA of player is " + f.randomPa());	}}